        public byte[] GetGameInfomations(int level)
        {
            using (var result = new PangyaBinaryWriter())
            {
                result.WriteUInt32(ConnectionID);
                if (level >= 1)
                {
                    result.Write(GetGameInfo());
                }

                if (level >= 2)
                {
                    result.Write(Inventory.GetCharData());
                }

                return result.GetBytes();
            }
        }

